import React from 'react';
import { Link, useParams } from 'react-router-dom';
import { Col, Row, Image, ListGroup, Card, Button, ListGroupItem } from 'react-bootstrap';
import Rating from '../components/Rating';
import products from '../products';

const ProductScreen = () => {
    const { id: productId } = useParams();
    const product = products.find((p) => p._id === productId);

    return (
        <>
            <Link className='btn btn-light my-3' to='/'>
                Go Back
            </Link>
            <Row>
                <Col md={5}>
                    {product ? (
                        <Image src={product.image} alt={product.name} fluid />
                    ) : (
                        <div>Product image not found</div>
                    )}
                </Col>
                <Col md={4}>
                    <ListGroup variant='flush'>
                        <ListGroupItem>
                            <h3>{product?.name || 'Product not found'}</h3>
                        </ListGroupItem>
                        <ListGroupItem>
                            <Rating value={product?.rating || 0} text={`${product?.numReviews || 0} reviews`} />
                        </ListGroupItem>
                        <ListGroupItem>
                            <ListGroupItem>Price: ${product?.price || 'N/A'}</ListGroupItem>
                        </ListGroupItem>
                    </ListGroup>
                </Col>
                <Col md={3}>
                    {/* ... other product details or actions ... */}
                </Col>
            </Row>
        </>
    );
};

export default ProductScreen;
